import React, { useState } from 'react';
import './App.css';

function App() {

  const [showMsg, setMsg] = useState(false);

  const handleClick = () => {
    setMsg(true);
  }

  const handleClose = () => {
    setMsg(false);
  }

  return (
    <div className="App" style={{backgroundImage: `url("https://weberr-screenshots.s3.ap-south-1.amazonaws.com/jasa.jpeg")`, height: '100vh', backgroundRepeat: 'no-repeat', backgroundSize: 'cover', backgroundPosition: 'center', position: 'relative'}}>
      <div style={{position: 'fixed', top: '15px', right: '15px', display: 'flex', alignItems: 'center'}} onClick={handleClick}>
        <img src="https://weberr-screenshots.s3.ap-south-1.amazonaws.com/ween.png" alt="icon" width="50" height="50" />
        <div style={{color: '#FFFC71', marginLeft: '5px', fontWeight: 'bold'}}>Message from Jack</div>
      </div>      
      {showMsg && <img onClick={handleClose} src="https://weberr-screenshots.s3.ap-south-1.amazonaws.com/msg.png" alt="msg" style={{position: 'absolute', width: '80%', left: '10%', height: '80%', top: '10%', borderRadius: '20px'}} />}
      <div>
        <div>Movie Dungeon</div>
        <div className="movies">
          <div class="card">
            <div class="content">
              <div class="front">
                Front
              </div>
              <div class="back">
                Back!
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
