import React, { useState } from 'react';

const Home = () => {
    const [showMsg, setMsg] = useState(false);

    const handleClick = () => {
        setMsg(true);
    }

    const handleClose = () => {
        setMsg(false);
    }

    return (
        <>
            <div style={{position: 'fixed', top: '15px', right: '15px', display: 'flex', alignItems: 'center', opacity: '0.8'}} onClick={handleClick}>
                <img src="https://weberr-screenshots.s3.ap-south-1.amazonaws.com/ween.png" alt="icon" width="50" height="50" />
                <div style={{color: '#FFFC71', marginLeft: '5px', fontWeight: 'bold'}}>Message from Jack</div>
            </div>      
            {showMsg && <img onClick={handleClose} src="https://weberr-screenshots.s3.ap-south-1.amazonaws.com/msg.png" alt="msg" style={{position: 'absolute', width: '80%', left: '10%', height: '80%', top: '10%', borderRadius: '20px'}} />}
            <div style={{position: 'fixed', bottom: '20px'}}>Movie Dungeon</div>
        </>
    )
}

export default Home;
