import React, { useState } from 'react';
import Home from './Home';
import Watched from './Watched';
import Picker from './Picker';
import './App.css';

function App(props) {

  const [screen, setScreen] = useState(0);
  const [dark, setDark] = useState(false);

  const handleWatched = () => {
    setScreen(1);
  }

  const handlePicker = () => {
    setScreen(2);
  }

  const goBack = () => {
    setScreen(0);
  }

  const handleDark = () => {
    setDark(true);
  }

  return (
    <div className="App" style={{backgroundImage: `url("https://weberr-screenshots.s3.ap-south-1.amazonaws.com/jasa.jpeg")`, height: '100vh', backgroundRepeat: 'no-repeat', backgroundSize: 'cover', backgroundPosition: 'center', position: 'relative'}}>
      {screen === 0 && <Home handleWatched={handleWatched} handlePicker={handlePicker} goDark={handleDark} />}
      {screen === 1 && <Watched goBack={goBack} />}
      {screen === 2 && <Picker goBack={goBack} />}
    </div>
  );
}

export default App;
